import React, { useState, useEffect } from "react";
function Quotes() {
	const [error, setError] = useState(null);
	const [isLoaded, setIsLoaded] = useState(false);
	const [quotes, setQuote] = useState([]);

	// Note: the empty deps array [] means
	// this useEffect will run once
	// similar to componentDidMount()
	useEffect(() => {
		fetch("/quote")
			.then((res) => res.json())
			.then(
				(result) => {
					setIsLoaded(true);
					setQuote(result);
				},
				// Note: it's important to handle errors here
				// instead of a catch() block so that we don't swallow
				// exceptions from actual bugs in components.
				(error) => {
					setIsLoaded(true);
					setError(error);
				}
			);
	}, []);

	if (error) {
		return <div>Error: {error.message}</div>;
	} else if (!isLoaded) {
		return <div>Loading...</div>;
	} else {
		return (
			<div className="container-fluid">
				<div className="row">
					{quotes.map((quote) => (
						<div className="col-sm">
							<div class="card">
								<h5 class="card-header mr-7">Job: {quote.Job} Id:{quote.Id}</h5>
								<div class="card-body">
									<h5 class="card-title">
										{quote.Qty} {quote.Product}
									</h5>
									<p class="card-text">${quote.EstRev}</p>
                  <div className="btn-group">
										<a href="/" class="btn btn-outline-success mr-4">
											Won
										</a>
										<a href="/" class="btn btn-outline-dark mr-4">
											Lost
										</a>
										<a href="/" class="btn btn-outline-warning mr-4">
											Edit
										</a>
										<a href="/" class="btn btn-outline-danger mr-4">
											Delete
										</a>
									</div>
								</div>
							</div>
						</div>
					))}
				</div>
			</div>
		);
	}
}
export default Quotes;
